  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"internationalization"}}><MDXTag name="strong" components={components} parentName="h1">{`Internationalization`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`A função de internacionalização permite salvar as `}<MDXTag name="em" components={components} parentName="p">{`strings`}</MDXTag>{`
do sistema de maneira centralizada e manutenivel alem de permitir
a mudança de linguagem em `}<MDXTag name="em" components={components} parentName="p">{`runtime`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"uso-básico"}}>{`Uso Básico`}</MDXTag>
<MDXTag name="p" components={components}>{`Após regisdtrar um conjunto de chaves`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    //registrando 
    t.register('pt-BR', {
        hello_world: 'Olá Mundo'
    });
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`O uso pode ser feito da seguinte forma`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    const localized = t("hello_world");

    console.log(localized); //Olá Mundo
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"váriaveis"}}>{`Váriaveis`}</MDXTag>
<MDXTag name="p" components={components}>{`Para cenários onde existem váriaveis dentro das mensagem, elas podem ser definidas
como váriaveis (nomeadas) e parametros (numerados).`}</MDXTag>
<MDXTag name="p" components={components}>{`Como no exemplo:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    //registrando 
    t.register('pt-BR', {
        hello_world: 'Olá {name}'
    });
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`O uso pode ser feito da seguinte forma`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    const localized = t("hello_world", { name:"Mundo" });

    console.log(localized); //Olá Mundo
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"parametros"}}>{`Parametros`}</MDXTag>
<MDXTag name="p" components={components}>{`Exemplo de uso com parametros:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    //registrando 
    t.register('pt-BR', {
        hello_world: 'Olá {0}' //o nomero é sequencial, {1}, {2}, {3}...
    });
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`O uso pode ser feito da seguinte forma`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    let parametro = "Mundo";
    const localized = t("hello_world", parametro);

    console.log(localized); //Olá Mundo
`}</MDXTag></MDXTag>
<MDXTag name="blockquote" components={components}>
<MDXTag name="p" components={components} parentName="blockquote">{`Esse modo é menos legivel e pode gerar confusões, use com cautela`}</MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"contagem-inteligente"}}>{`Contagem Inteligente`}</MDXTag>
<MDXTag name="p" components={components}>{`Para cenários onde temos contagem de elementos, podemos usar
a variavel ou parametro para contagem`}</MDXTag>
<MDXTag name="p" components={components}>{`Exemplo:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    //registrando 
    t.register('pt-BR', {
        items_count: [
            "Sem Items!",
            "Apenas um Item!",
            "São {count} Items!"
        ]
    });


`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`O uso pode ser feito da seguinte forma`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    const localizedNone = t("hello_world", { count: 0 });
    const localizedOne = t("hello_world", { count: 1 });
    const localizedMany = t("hello_world", { count: 2 });

    console.log(localizedNone); //Sem Items!
    console.log(localizedOne);  //Apenas um Item!
    console.log(localizedMany); //São 2 Items!
`}</MDXTag></MDXTag>
<MDXTag name="blockquote" components={components}>
<MDXTag name="p" components={components} parentName="blockquote">{`O número de items acompanha os items do array, se existirem 4 items, por exemplo,
será o primeiro para `}<MDXTag name="strong" components={components} parentName="p">{`0`}</MDXTag>{` o segundo para `}<MDXTag name="strong" components={components} parentName="p">{`1`}</MDXTag>{` o terceiro para `}<MDXTag name="strong" components={components} parentName="p">{`2`}</MDXTag>{` e o quarto para todos os outros
O mesmo ocorreria para `}<MDXTag name="strong" components={components} parentName="p">{`2`}</MDXTag>{` itens, o primeiro para `}<MDXTag name="strong" components={components} parentName="p">{`0`}</MDXTag>{` e o segundo para todos os outros`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`A contagem inteligente funciona para as props com os seguintes nomes:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="strong" components={components} parentName="li">{`count`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="strong" components={components} parentName="li">{`number`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="strong" components={components} parentName="li">{`size`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="strong" components={components} parentName="li">{`length`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="strong" components={components} parentName="li">{`items`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`Ou para o primeiro item de um `}<MDXTag name="em" components={components} parentName="p">{`array`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"mudança-de-linguagem"}}>{`Mudança de linguagem`}</MDXTag>
<MDXTag name="p" components={components}>{`Exemplo de uso com varios idiomas:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    //registrando 
    t.register('pt-BR', {
        hello_world: 'Olá Mundo' //o nomero é sequencial, {1}, {2}, {3}...
    });
      //registrando 
    t.register('en-US', {
        hello_world: 'Hello World' //o nomero é sequencial, {1}, {2}, {3}...
    });
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`O uso pode ser feito da seguinte forma`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`    import t from '@nimbos/i18n';

    let parametro = "Mundo";
    const localizedPTBR = t("hello_world");
    t.lang("en-US");
    const localizedENUS = t("hello_world");

    console.log(localizedPTBR); //Olá Mundo
    console.log(localizedENUS); //Hello World
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"exemplo-no-react"}}>{`Exemplo no React`}</MDXTag>
<MDXTag name="p" components={components}>{`Pode ser usado como função:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx","metastring":null}}>{`import t from '@nimbos/i18n'


<div>
    <h1>{t\`view.title\`}</h1>
    <p>{t('view.present', { name: user.name, email: user.email })}</p>
    <span>{t('statistics.online_users', onlineUsers)}</span>
</div>
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Ou como componente:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx","metastring":null}}>{`import {T} from '@nimbos/i18n'
// ou
//import {I18n} from '@nimbos/i18n'

<div>
    <h1><T>view.title</T></h1>
    <p>
        <T 
            name={user.name}
            email={user.email} >
            view.present
        </T>
    </p>
    <span><T params={[onlineUsers]}>statistics.online_users</T> </span>
</div>
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
